mapper.insert:
INSERT INTO use_room(
	boo_code
	, room_type_code
	, e_id
	, use_room_rate
	, use_room_condition
)VALUES(
	(SELECT boo_code FROM booking ORDER BY boo_code DESC LIMIT 1)
	, #{roomTypeCode}
	, #{eId}
	, #{roomSize}
	, #{cateCode}
)

INSERT INTO use_extra(
	boo_code
	, extra_code
	, e_id
	, use_extra_date
	, use_extra_rate
	, use_extra_condition
)VALUES(
	(SELECT boo_code FROM booking ORDER BY boo_code DESC LIMIT 1)
	, #{extraName}
	, #{eId}
	, #{extraDate}
	, #{extraRate}
	, #{cateCode}
)

INSERT INTO booking(
	e_id
	, boo_date
	, boo_in
	, boo_night
	, boo_count
	, boo_path
	, boo_memo
)VALUES(
	#{eId}
	, now()
	, #{booIn}
	, #{booNight}
	, #{booCount}
	, #{booPath}
	, #{booMemo}
)
