mapper.select:
SELECT COUNT(*) FROM category

	WHERE ${cate} = #{input}
	
		LIMIT ${start}, ${pagePerRow}

SELECT cate_code , cate_large , cate_small , cate_name , e_name e_id , cate_date FROM category LEFT JOIN employee ON category.e_id = employee.e_id
WHERE category.${cate} = #{input}
ORDER BY cate_small
LIMIT ${start}, ${pagePerRow}

