mapper.select:
SELECT g_code ,g_gender ,cate_name g_nation ,e_name e_id ,g_name ,g_birthdate ,g_job ,g_phone ,g_email ,g_date FROM guest LEFT JOIN employee ON guest.e_id = employee.e_id LEFT JOIN category ON guest.g_nation = category.cate_code
WHERE guest.${cate} = #{input}
ORDER BY g_code DESC LIMIT ${start}, ${pagePerRow}

mapper.insert:
INSERT INTO guest(
	g_gender
	,g_nation
	,e_id
	,g_name
	,g_birthdate
	,g_job
	,g_phone
	,g_email
	,g_date
)VALUES(
	#{gGender}
	
		, (SELECT cate_code FROM category WHERE cate_name = 'korea')
	
	
		, #{gNation}
	
	,#{eId}
	,#{gName}
	,#{gBirthdate}
	,#{gJob}
	,#{gPhone}
	,#{gEmail}
	,now()
)

mapper.update:
UPDATE guest
SET
	g_gender = #{gGender}
	, g_Nation = #{gNation}
	, g_name = #{gName}
	, g_birthdate = #{gBirthdate}
	, g_job = #{gJob}
	, g_phone = #{gPhone}
	, g_email = #{gEmail}
WHERE g_code=#{gCode}
