mapper.insert:
INSERT INTO booking(
	e_id
	, boo_date
	, boo_in
	, boo_out
	, boo_count
	, boo_path
	, boo_memo
	, boo_condition
)VALUES(
	#{eId}
	, now()
	, #{booIn}
	, #{booIn} + INTERVAL #{booOut} DAY
	, #{booCount}
	, #{booPath}
	, #{booMemo}
	, '예약'
)

mapper.update:
UPDATE booking
LEFT JOIN booking_room ON booking.boo_code = booking_room.boo_code
SET boo_condition=#{condition}

	WHERE boo_room_code = #{booRoomCode}


	WHERE booking.boo_code = #{booCode}
