mapper.select:
SELECT COUNT(*) FROM booking

	
		WHERE booking.${cate} = #{input}
	
	
		WHERE DATE_FORMAT(boo_date, '%Y-%m-%d') = #{input}

SELECT booking.boo_code , e_name e_id , DATE_FORMAT(boo_date, '%Y-%m-%d %H:%i') boo_date , boo_in , boo_night , boo_count , boo_path , boo_condition FROM booking LEFT JOIN employee ON booking.e_id = employee.e_id
WHERE booking.${cate} = #{input}
WHERE DATE_FORMAT(boo_date, '%Y-%m-%d') = #{input}
WHERE boo_condition = '예약'
ORDER BY booking.boo_code DESC
LIMIT ${start}, ${pagePerRow}

mapper.insert:
INSERT INTO booking(
	e_id
	, boo_date
	, boo_in
	, boo_night
	, boo_count
	, boo_path
	, boo_memo
	, boo_condition
)VALUES(
	#{eId}
	, now()
	, #{booIn}
	, #{booNight}
	, #{booCount}
	, #{booPath}
	, #{booMemo}
	, '예약'
)
