mapper.insert:
INSERT INTO keep(
k_code
, e_id
, k_name
, k_condition
, k_datetime
, k_site
, k_memo
)
Values(
#{kCode}
, #{eId}
, #{kName}
, #{kCondition}
, now()
, #{kSite}
, #{kMemo}
)

mapper.update:
Update keep
SET
	k_name = #{kName}
	, k_condition = #{kCondition}
	, k_site = #{kSite}
	, k_memo = #{kMemo}
WHERE
	k_code = #{kCode}
