mapper.select:
SELECT COUNT(*) FROM payment

	
		WHERE payment.${cate} = #{input}
	
	
		WHERE DATE_FORMAT(p_date, '%Y-%m-%d') = #{input}

SELECT p_code , boo_code , e_name e_id , p_condition , p_method , DATE_FORMAT(p_date, '%Y-%m-%d %H:%i') p_date , p_rate , p_memo FROM payment LEFT JOIN employee ON employee.e_id = payment.e_id
WHERE payment.${cate} = #{input}
WHERE DATE_FORMAT(p_date, '%Y-%m-%d') = #{input}
ORDER BY p_code DESC
LIMIT ${start}, ${pagePerRow}

mapper.insert:
INSERT INTO payment(
